mapper.select:
SELECT * FROM tb_comments WHERE 1=1
AND article_id = #{article_id}
AND sort = #{sort}

SELECT * FROM tb_comments WHERE p_id = 0 AND c_id = 0
AND article_id=#{article_id}
AND sort = #{sort}

mapper.insert:
INSERT INTO tb_comments (
  p_id, c_id, author_avatar, article_id, author, author_id, email, content,
  `time`, url, state, sort
)VALUES (
  #{p_id}, #{c_id}, #{author_avatar}, #{article_id}, #{author}, #{author_id}, #{email}, #{content},
  #{time}, #{url}, #{state}, #{sort}
)

mapper.update:
UPDATE tb_comments
p_id=#{p_id}, c_id=#{c_id}, author_avatar=#{author_avatar}, article_id=#{article_id}, author=#{author}, author_id=#{author_id}, email=#{email}, content=#{content}, `time`=#{time}, url=#{url}, state=#{state}, sort=#{sort},
WHERE id = #{id}
